<template lang="pug">
div
  //- img(:src="imgSrc")
</template>
<script>
export default {
  props: {
    inputAllowDele: { type: Boolean, default: false },
    inputDestroy: { type: Boolean, default: true }
  },
  data () {
    return {
      maskDiv: {},
      imgSrc: 'data:image/png;base64,',
      bgurl: 'url(\'data:image/png;base64,\')'
    }
  },
  mounted () {
    setTimeout(() => {
      this.init()
    }, 100)

    if (!this.inputAllowDele) {
      this.Monitor()
    }
  },
  methods: {
    init () {
      const canvas = document.createElement('canvas')
      canvas.id = 'canvas'
      canvas.width = '300'
      canvas.height = '300'

      this.maskDiv = document.createElement('div')

      const img = document.createElement('img')
      let str = this.imgSrc.replace(/\. +/g, '')
      str = str.replace(/[\r\n]/g, '')
      img.src = str

      canvas.getContext('2d').drawImage(img, 0, 0, 150, 50)

      // let src = canvas.toDataURL('image/png')
      // src = src.replace(/=+$/, '')
      this.maskDiv.style.position = 'fixed'
      this.maskDiv.style.zIndex = '9999'
      this.maskDiv.id = '_acmepoint'
      this.maskDiv.style.top = '40%'
      this.maskDiv.style.left = '60%'
      this.maskDiv.style.height = '10%'
      this.maskDiv.style.width = '12%'
      this.maskDiv.style.pointerEvents = 'none'
      this.maskDiv.style.backgroundImage = this.bgurl
      // this.maskDiv.style.backgroundImage = `url('${src}')`
      this.maskDiv.style.backgroundRepeat = 'no-repeat'
      this.maskDiv.style.backgroundSize = '150px 50px'
      // 水印节点插到body下
      // console.log(this.maskDiv.style.backgroundImage)
      // console.log(`url('${src}')`)
      const node = document.createTextNode('Hello World')
      this.$refs.chart.appendChild(node)
      document.body.appendChild(this.maskDiv)
    },
    Monitor () {
      const body = document.getElementsByTagName('body')[0]
      const options = {
        childList: true,
        attributes: true,
        characterData: true,
        subtree: true,
        attributeOldValue: true,
        characterDataOldValue: true
      }
      const observer = new MutationObserver(this.callback)
      observer.observe(body, options) // 监听body节点
    },
    // DOM改变执行callback
    callback (mutations, observer) {
      // 当attribute属性被修改
      if (mutations[0].target.id === '_acmepoint') {
        this.removeMaskDiv()
      }
      // 当id被改变时
      if (mutations[0].attributeName === 'id') {
        this.removeMaskDiv()
        this.init()
      }
      // 当节点被删除
      if (mutations[0].removedNodes[0] && mutations[0].removedNodes[0].id === '_acmepoint') {
        this.init()
      }
    },
    removeMaskDiv () {
      document.body.removeChild(this.maskDiv)
    },
    createMaskDiv () {
      this.init()
    }
  },
  watch: {
    inputText () {
      this.$nextTick(() => {
        this.removeMaskDiv()
      })
    }
  },
  destoryed () {
    if (this.inputDestroy) {
      this.removeMaskDiv()
    }
  }
}
</script>

